mapper.select:
SELECT *
FROM td_index_basic
WHERE F16013 = #{F16013}
AND MARKET_ID = #{market_id}

        SELECT concat(substr(lpad(F20004,8, '0'), 1, 2), ':',substr(lpad(F20004,8, '0'), 3, 2), ':',substr(lpad(F20004,8, '0'), 5, 2)) AS F20004
               ,DATE_FORMAT(F20044, "%Y.%m.%d") as F20044
		       ,F20008
        FROM td_index_intra
        WHERE F16013 = #{F16013}
        AND market_id = #{market_id}
        ORDER BY F20044 desc, F20004 desc LIMIT ${limit}

        SELECT DATE_FORMAT(F12506, "%Y.%m.%d") as F12506
		       ,F15001
        FROM td_index_hist
        WHERE F16013 = #{F16013}
        AND market_id = #{market_id}
        ORDER BY F12506 desc LIMIT ${limit}

SELECT * FROM td_index_basic WHERE large_type = #{large_type}
AND middle_type like CONCAT('%',#{middle_type},'%')
ORDER BY F16013 ASC

SELECT *, (SELECT B.F15001 FROM td_index_hist B WHERE B.market_id = A.market_id AND B.F16013 = A.F16013 AND B.F12506 <= #{bef1Week} ORDER BY B.F12506 DESC LIMIT 1) AS bef1Week, (SELECT B.F15001 FROM td_index_hist B WHERE B.market_id = A.market_id AND B.F16013 = A.F16013 AND B.F12506 <= #{bef1Month} ORDER BY B.F12506 DESC LIMIT 1) AS bef1Month, (SELECT B.F15001 FROM td_index_hist B WHERE B.market_id = A.market_id AND B.F16013 = A.F16013 AND B.F12506 <= #{befYtd} ORDER BY B.F12506 DESC LIMIT 1) AS befYtd, (SELECT B.F15001 FROM td_index_hist B WHERE B.market_id = A.market_id AND B.F16013 = A.F16013 AND B.F12506 <= #{bef1Year} ORDER BY B.F12506 DESC LIMIT 1) AS bef1Year, (SELECT B.F15001 FROM td_index_hist B WHERE B.market_id = A.market_id AND B.F16013 = A.F16013 AND B.F12506 <= #{bef3Year} ORDER BY B.F12506 DESC LIMIT 1) AS bef3Year, (SELECT B.F15001 FROM td_index_hist B WHERE B.market_id = A.market_id AND B.F16013 = A.F16013 AND B.F12506 <= #{bef5Year} ORDER BY B.F12506 DESC LIMIT 1) AS bef5Year, (SELECT B.F15001 FROM td_index_hist B WHERE B.market_id = A.market_id AND B.F16013 = A.F16013 AND B.F12506 <= #{bef10Year} ORDER BY B.F12506 DESC LIMIT 1) AS bef10Year FROM td_index_basic A WHERE A.large_type = #{large_type}
AND A.middle_type like CONCAT('%',#{middle_type},'%')
ORDER BY A.F15023 DESC

SELECT *
FROM td_etp_basic
WHERE F16013 = #{F16013}

SELECT DATE_FORMAT(F20044, "%Y.%m.%d") as F20044, concat(substr(lpad(F20004,8, '0'), 1, 2), ':',substr(lpad(F20004,8, '0'), 3, 2), ':',substr(lpad(F20004,8, '0'), 5, 2)) AS F20004 ,F20008, F20041, F40544, F40545, F33294, F15301, F15302, F15303, F30818 FROM td_etp_intra WHERE F16013 = #{F16013}
ORDER BY F20044 desc, F20004 desc LIMIT ${limit}
ORDER BY F20044 desc, F20004 desc LIMIT 100

        SELECT concat(substr(lpad(A.F20004,8, '0'), 1, 2), ':',substr(lpad(A.F20004,8, '0'), 3, 2), ':',substr(lpad(A.F20004,8, '0'), 5, 2)) AS F20004
               ,DATE_FORMAT(B.F20044, "%Y.%m.%d") as F20044
		       ,A.F20008 as F20008, B.F20008 as iF20008
        FROM td_etp_intra A, td_index_intra B
        WHERE A.F16013 = #{F16013}
        AND B.F16013 = #{F16257}
        AND B.market_id = #{market_id}
        AND A.F20044 = B.F20044
        AND A.F20004 = B.F20004
        ORDER BY F20044 desc, F20004 desc LIMIT ${limit}

        SELECT DATE_FORMAT(F12506, "%Y.%m.%d") as F12506
		       ,F15001, F15318 as iF15001
        FROM td_etp_hist
        WHERE F16013 = #{F16013}


            ORDER BY F12506 desc LIMIT ${limit}


            ORDER BY F12506 desc

        SELECT DATE_FORMAT(F12506, "%Y.%m.%d") as F12506
		       ,F15001, F15318 as iF15001
        FROM td_etp_hist
        WHERE F16013 = #{F16013}
        ORDER BY F12506 desc LIMIT ${limit}

        SELECT DATE_FORMAT(A.F12506, "%Y.%m.%d") as F12506
		       ,A.F15001 as F15001, B.F15001 as iF15001
        FROM td_etp_hist A, td_index_hist B
        WHERE A.F16013 = #{F16013}
        AND B.F16013 = #{F16257}
        AND B.market_id = #{market_id}
        AND A.F12506 = B.F12506
        ORDER BY F12506 desc LIMIT ${limit}

mapper.delete:
DELETE FROM tm_favor_item
 WHERE TYPE_CD = #{type_cd}
   AND INST_CD =  #{inst_cd}
   AND EMAIL = #{user_id}


   AND F16012 = #{jisu_cd}


    AND F16013 = #{jisu_cd}
    AND market_id = #{market_id}


    AND F16012 = '-'

mapper.select:
