,base.F33929
FROM kc_etp.td_etp_basic base LEFT OUTER JOIN (SELECT * FROM tm_favor_item faver WHERE faver.gubun = 1 AND type_cd = #{type_cd} AND inst_cd = #{inst_cd} AND email = #{user_id} ) faver ON base.F16012 = faver.F16012 WHERE (base.F16493 = '1' OR base.F16493 = '2') AND base.F33960 = #{krx_cd}

SELECT if (faver.F16012 = base.F16012, '1', '0') faver ,faver.item_seq faver_seq ,base.F12506 ,base.F16012 as JISU_CD ,base.F16012 /* 국제표준코드 */ ,base.F16013 ,base.F16002 as JISU_NM ,base.F16003 ,base.F16017 ,base.F15001 ,base.F15472 ,base.F15004 ,base.F15006 ,base.F15009 ,base.F15010 ,base.F15011 ,base.F15015 ,base.F15023 ,base.F15028 ,base.F15029 ,base.F30812 ,base.F30813 ,base.F18438 ,base.F16143 ,base.F16073 ,base.F15007 ,base.F16493 ,base.F15301 ,base.F15302 ,base.F15303 ,base.F15304 ,base.F15305 ,base.F30818 ,base.F15318 ,base.F16497 ,base.F31892 ,base.F18450 ,base.F18001 ,base.F16500 ,base.F33307 ,base.F33308 ,base.F33951 ,base.F15319 ,base.F30823 ,base.F15602 ,base.F15631 ,base.F15632 ,base.F15633 ,base.F19288 ,base.F34515 ,base.F16499 ,base.F03329 ,base.F18439 ,base.F16109 ,base.F16257 ,base.F34777 ,base.F34521 ,base.F34776 ,base.F34514 ,base.F34239 ,base.F34241 ,base.F34769 ,base.F34770 ,base.F34771 ,base.F34772 ,base.F34775 ,base.F34778 ,base.F34779 ,base.F34780 ,base.F34781 ,base.F34782 ,base.F34783 ,base.F33960 ,base.F33961
,base.F33929
FROM kc_etp.td_etp_basic base LEFT OUTER JOIN (SELECT * FROM tm_favor_item faver WHERE faver.gubun = 1 AND type_cd = #{type_cd} AND inst_cd = #{inst_cd} AND email = #{user_id} ) faver ON base.F16012 = faver.F16012 where (F16493 = '3' OR F16493 = '4') AND base.F33960 = #{krx_cd}

SELECT faver ,faver_seq ,A.JISU_CD ,A.JISU_NM ,A.INDEX_CAL_METHOD ,A.MARKET_ID ,A.ETP_MARKET_ID ,A.LARGE_TYPE ,A.MIDDLE_TYPE ,A.F16013 /* 단축코드 */ ,A.F15001 /* 현재가 */ ,A.F15007 /* 기준가 */ ,A.F15472 /* 대비 */ ,A.F15004 /* 등락률 */ ,A.F15028 /* 시가총액 */ ,A.F16012 AS F16012 /* 국제표준코드 */ ,A.F16257 AS F16257 /* ETP기초지수코드 */ ,A.F34239 AS F34239 /* ETP기초지수MID */ FROM (SELECT if (faver.F16013 = BAS.F16013 AND BAS.market_id = faver.market_id, '1', '0') faver ,faver.item_seq faver_seq ,BAS.F16013 JISU_CD ,BAS.F16002 JISU_NM ,BAS.F12506 IP_DT ,BAS.ANNO_DATE ,BAS.INDEX_CAL_METHOD ,BAS.MARKET_ID ,BAS.LARGE_TYPE ,BAS.MIDDLE_TYPE ,BAS.F16013 /* 단축코드 */ ,BAS.F15001 /* 현재가 */ ,BAS.F15007 /* 기준가 */ ,BAS.F15472 /* 대비 */ ,BAS.F15004 /* 등락률 */ ,BAS.F15028 /* 시가총액 */ ,CAST(substr(BAS.MARKET_ID, 2) AS SIGNED) ETP_MARKET_ID ,'' AS F16012 /* 국제표준코드 */ , BAS.F16013 AS F16257 /* ETP기초지수코드 */ , LPAD( SUBSTR( BAS.MARKET_ID, 2 ), 3, '0' ) AS F34239 /* ETP기초지수MID */ FROM td_index_basic BAS LEFT OUTER JOIN (SELECT * FROM tm_favor_item WHERE gubun = 2 AND type_cd = #{type_cd} AND inst_cd = #{inst_cd} AND email = #{user_id} ) faver ON BAS.F16013 = faver.F16013 AND BAS.MARKET_ID = faver.MARKET_ID
WHERE BAS.large_type = #{large_type}
) A ORDER BY A.F15028 desc

SELECT AVG(${colName}) as ${colName}
FROM td_etp_hist
WHERE F16013 = #{F16013}
ORDER BY F12506 desc
LIMIT ${num}

SELECT AVG(${colName}) as ${colName}
FROM td_etp_intra
WHERE F16013 = #{F16013}
AND F20044 = #{F12506}
AND ${colName} != 0
