select.if:
SELECT /* etpDetail.getEtpChartData (#{term}) ETP 차트 정보를 조회한다. */
etp_info.yyyymmdd AS yyyymmdd /* 입회일자 */ , etp_info.fmt_yyyymmdd AS fmt_yyyymmdd /* 입회일자 */ , etp_info.hh24 AS hh24 /* 체결인트라생성시각 */ , IFNULL( CAST( etp_info.now_price as unsigned ) , 0 ) AS now_price /* 인트라종가 */ , etp_info.etp_nm AS etp_nm /* 한글종목명 */
, IFNULL( CAST( index_info.now_price as unsigned ) , 0 ) AS index_now_price /* 인덱스 인트라 종가 */ , index_info.index_nm AS index_nm /* 인덱스 한글종목명 */
FROM ( SELECT td_etp_hist.F12506 AS yyyymmdd /* 입회일자 */ , DATE_FORMAT( td_etp_hist.F12506, "%Y.%m.%d" ) AS fmt_yyyymmdd /* 입회일자 */ , '00' AS hh24 /* 생성시각 */ , td_etp_hist.F15001 AS now_price /* 현재가 */ , td_etp_basic.F16002 AS etp_nm /* 한글종목명 */
FROM kc_etp.td_etp_basic INNER JOIN td_etp_hist ON ( td_etp_basic.F16013 = td_etp_hist.F16013 /* 단축코드 */
/* 1달 */ AND td_etp_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 1 MONTH ), '%Y%m%d' )
/* 3달 */ AND td_etp_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 3 MONTH ), '%Y%m%d' )
/* 6달 */ AND td_etp_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 6 MONTH ), '%Y%m%d' )
/* 1년 */ AND td_etp_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 1 YEAR ), '%Y%m%d' )
/* 전체 */
/* 1달 */ AND td_etp_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 1 MONTH ), '%Y%m%d' )
)
WHERE 1 = 1 AND td_etp_basic.F16012 = #{basicData.f16012} /* 국제표준코드 */
) etp_info LEFT JOIN ( SELECT td_index_hist.F12506 AS yyyymmdd /* 입회일자 */ , DATE_FORMAT(td_index_hist.F12506, "%Y.%m.%d") AS fmt_yyyymmdd /* 입회일자 */ , '00' AS hh24 /* 생성시각 */ , td_index_hist.F15001 AS now_price /* 현재가 */ , td_index_basic.F16002 AS index_nm /* 한글종목명 */
FROM td_index_basic INNER JOIN td_index_hist ON ( td_index_basic.F16013 = td_index_hist.F16013 /* 단축코드 */ AND td_index_basic.MARKET_ID = td_index_hist.MARKET_ID /* 시장 ID */
/* 1달 */ AND td_index_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 1 MONTH ), '%Y%m%d' )
/* 3달 */ AND td_index_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 3 MONTH ), '%Y%m%d' )
/* 6달 */ AND td_index_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 6 MONTH ), '%Y%m%d' )
/* 1년 */ AND td_index_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 1 YEAR ), '%Y%m%d' )
/* 전체 */
/* 1달 */ AND td_index_hist.F12506 >= DATE_FORMAT( DATE_SUB( NOW(), INTERVAL 1 MONTH ), '%Y%m%d' )
)
WHERE 1 = 1 AND td_index_basic.F16013 = #{basicData.f16257} /* 단축코드 = ETP기초지수코드 */ AND td_index_basic.MARKET_ID LIKE CONCAT( '_' , LPAD( #{basicData.f34239}, 3, '0' ) , '%' ) /* 시장 ID = ETP기초지수MID */
) index_info ON ( etp_info.yyyymmdd = index_info.yyyymmdd /* 입회일자 */ )
WHERE 1 = 1
