SELECT /* etpOper.geEtpOperIndexOversea ETP 운용관리 - 지수관리 - 해외지수 종가 모니터링을 조회한다. */
td_index_basic.F12506 AS f12506 /* 입회일 */ , DATE_FORMAT( td_index_basic.F12506, '%Y.%m.%d' ) AS fmt_f12506 /* 입회일 */ , td_index_basic.F16013 AS f16013 /* 단축코드 */ , td_index_basic.F16002 AS f16002 /* 한글종목명 */ , td_index_basic.F15001 AS f15001 /* 현재가 */ , td_index_basic.F15004 AS f15004 /* 등락율 */ , td_index_basic.large_type AS large_type /* 지수대분류(FNGUIDE, KRX, KIS, KAP) */ , td_index_basic.market_id AS market_id /* 시장 ID */ , td_index_basic.std_date AS std_date /* 기준일 */ , DATE_FORMAT( td_index_basic.std_date, '%Y.%m.%d' ) AS fmt_std_date /* 기준일 */
, '' AS in_out /* 입수 구분 */ , td_index_basic.f16002 AS f16002 /* 한글종목명 */ , td_index_basic.f16013 AS f16013 /* 단축코드 */ , '' AS incre_symbol /* 증가 심볼 */ , '' AS rest_date /* 휴장일 */ , td_index_basic.f12506 AS f12506 /* 입회일 */ , td_index_basic.f15001 AS f15001 /* 현재가 */ , td_index_basic.std_date AS std_date /* 기준일자 */
FROM td_index_basic WHERE 1 = 1
AND td_index_basic.f12506 IN ( SELECT MAX( f12506 ) FROM td_index_basic )
/* 해외지수 mid 에 존재하는 정보만 추출 */ AND EXISTS (
SELECT 1 FROM DUAL WHERE td_index_basic.market_id LIKE CONCAT( '_', LPAD( #{item.jisu_mid}, 3, '0' ), '%' )
)
/* 해당 지수가 td_etp_basic 에 속하고 로그인 운용사 코드와 동일한 정보만 추출 */ AND EXISTS ( SELECT 1 FROM td_etp_basic WHERE td_index_basic.F16013 = td_etp_basic.f16257 /* 단축코드 = ETP기초지수코드 */ AND td_index_basic.MARKET_ID LIKE CONCAT( '_' , LPAD( td_etp_basic.f34239, 3, '0' ) , '%' ) /* 시장 ID = ETP기초지수MID */
AND td_etp_basic.f33960 = #{krx_cd} /* ETP운용사코드 가 로그인한 운용사 코드와 동일한 정보만 추출 */ )
