mapper.select:
SELECT /* etpinfo.getJisuListByCtgCode 분류코드별 지수정보를 조회한다. */
tm_etpctg_code.ctg_large_code AS ctg_large_code /* ETP대분류코드 */ , tm_etpctg_code.ctg_code AS ctg_code /* ETP분류코드 */ , tm_etpctg_code.ctg_name AS ctg_name /* 분류이름 */
FROM tm_etpctg_code WHERE tm_etpctg_code.ctg_large_code = #{ctg_large_code}
/* tm_etpctg_map 에 존재하는 경우만 조회 */ AND EXISTS ( SELECT 1 FROM tm_etpctg_map
WHERE tm_etpctg_map.ctg_large_code = tm_etpctg_code.ctg_large_code AND tm_etpctg_map.ctg_code = tm_etpctg_code.ctg_code
LIMIT 0, 1 )
/* 제외할 중분류 정보( com_val01, com_val02) 에 속하는 경우 코드 제외 */ AND NOT EXISTS ( SELECT 1 FROM tm_code_dtl WHERE tm_code_dtl.com_mst_cd = 'COM004' /* ETP 제외할 중분류 정보 */ AND tm_code_dtl.com_val01 = tm_etpctg_code.ctg_large_code /* ETP대분류코드 */ AND tm_code_dtl.com_val02 = tm_etpctg_code.ctg_code /* ETP분류코드 */ )
ORDER BY tm_etpctg_code.ctg_large_code , tm_etpctg_code.ctg_code

SELECT SUM(F16500) * 1000000 + SUM(F18001) AS F16500
FROM td_etp_basic
WHERE F16257 = #{F16257}
AND F34239 = #{F34239}
AND F16493 IN (1,2)

SELECT SUM(F16500) * 1000000 + SUM(F18001) AS F16500
FROM td_etp_basic
WHERE F16257 = #{F16257}
AND F34239 = #{F34239}
AND F16493 IN (3,4)

SELECT *
FROM tm_etpctg_code
WHERE ctg_large_code = #{ctg_large_code}
AND ctg_code = #{ctg_code}

SELECT SUM(A.F16500) * 1000000 + SUM(A.F18001) AS F16500
FROM td_etp_basic A, tm_etpctg_map B
WHERE B.ctg_large_code = #{ctg_large_code}
AND B.ctg_code = #{ctg_code}
AND B.F16012 = A.F16012
AND A.F16493 IN (1,2)

SELECT SUM(A.F16500) * 1000000 + SUM(A.F18001) AS F16500
FROM td_etp_basic A, tm_etpctg_map B
WHERE B.ctg_large_code = #{ctg_large_code}
AND B.ctg_code = #{ctg_code}
AND B.F16012 = A.F16012
AND A.F16493 IN (3,4)

SELECT COUNT(A.F15004) AS Up
FROM td_etp_basic A, tm_etpctg_map B
WHERE B.ctg_large_code = #{ctg_large_code}
AND B.ctg_code = #{ctg_code}
AND B.F16012 = A.F16012
AND A.F15004 > 0


SELECT COUNT(A.F15004) AS Down
FROM td_etp_basic A, tm_etpctg_map B
WHERE B.ctg_large_code = #{ctg_large_code}
AND B.ctg_code = #{ctg_code}
AND B.F16012 = A.F16012
AND A.F15004 < 0


SELECT COUNT(A.F15004) AS Bohap
FROM td_etp_basic A, tm_etpctg_map B
WHERE B.ctg_large_code = #{ctg_large_code}
AND B.ctg_code = #{ctg_code}
AND B.F16012 = A.F16012
AND A.F15004 = 0
