mapper.select:
SELECT  COUNT(1)   AS  cnt
  FROM  tm_jisu_mast
 WHERE  tm_jisu_mast.jisu_id                =   #{jisu_id}

SELECT tm_jisu_temp_upload.file_id AS file_id , tm_jisu_temp_upload.row_no AS row_no
, tm_jisu_temp_upload.col01 AS col01 , tm_jisu_temp_upload.col02 AS col02 , tm_jisu_temp_upload.col03 AS col03 , tm_jisu_temp_upload.col04 AS col04 , tm_jisu_temp_upload.col05 AS col05
FROM tm_jisu_temp_upload WHERE tm_jisu_temp_upload.file_id = #{file_id}

mapper.insert:
INSERT  INTO    tm_jisu_file 
( 
        org_file_name
    ,   save_file_name
    ,   file_size
    ,   mime_type
    ,   gubun

    ,   reg_id 
    ,   reg_time 
) 
VALUES 
( 
        #{org_file_name}
    ,   #{save_file_name}
    ,   #{file_size}
    ,   #{mime_type}
    ,   #{gubun}

    ,   #{user_id}
    ,   now() 
)

INSERT  INTO    tm_jisu_temp_upload 
( 
        file_id
    ,   row_no

    ,   col01
    ,   col02
    ,   col03
    ,   col04
    ,   col05

    ,   reg_id 
    ,   reg_time 
) 
VALUES 

( 
        ${file_id}
    ,   ${data.row_no}

    ,   #{data.col01}
    ,   #{data.col02}
    ,   #{data.col03}
    ,   #{data.col04}
    ,   #{data.col05}

    ,   #{user_id}
    ,   now() 
)

INSERT  INTO    tm_jisu_upload 
( 
        jisu_seq
    ,   row_no

    ,   jisu_file_id
    ,   col01
    ,   col02
    ,   col03
    ,   col04
    ,   col05

    ,   reg_id 
    ,   reg_time 
) 
VALUES 

( 
        #{jisu_seq}
    ,   ${data.row_no}

    ,   ${jisu_file_id}
    ,   #{data.col01}
    ,   #{data.col02}
    ,   #{data.col03}
    ,   #{data.col04}
    ,   #{data.col05}

    ,   #{user_id}
    ,   now() 
)

INSERT  INTO    tm_jisu_upload_hist
( 
        hist_no
    ,   jisu_seq
    ,   row_no

    ,   jisu_file_id
    ,   col01
    ,   col02
    ,   col03
    ,   col04
    ,   col05

    ,   reg_id 
    ,   reg_time 
) 
VALUES 

( 
        ${hist_no}
    ,   #{jisu_seq}
    ,   ${data.row_no}

    ,   ${file_id}
    ,   #{data.col01}
    ,   #{data.col02}
    ,   #{data.col03}
    ,   #{data.col04}
    ,   #{data.col05}

    ,   #{user_id}
    ,   now() 
)

INSERT  INTO    tm_jisu_share_req
( 
        jisu_seq
    ,   inst_cd

    ,   jisu_id
    ,   req_flag
    ,   reg_id 
    ,   reg_time
    ,   upd_id
    ,   upd_time
) 
VALUES 

( 
        #{jisu_seq}
    ,   #{item}

    ,   #{jisu_id}
    ,   #{req_flag}
    ,   #{user_id}
    ,   now()
    ,   #{user_id}
    ,   now()
)

INSERT  INTO    tm_jisu_mast 
( 
        jisu_id 
    ,   jisu_kor_nm 
    ,   jisu_eng_nm 
    ,   jisu_summary 
    ,   base_jisu 
    ,   base_date 
    ,   method_file_id
    ,   jisu_file_id
    ,   req_content 
    ,   status

    ,   reg_id 
    ,   reg_time 
    ,   upd_id 
    ,   upd_time 
) 
VALUES    
( 
        #{jisu_id}
    ,   #{jisu_kor_nm}
    ,   #{jisu_eng_nm}
    ,   #{jisu_summary}
    ,   #{base_jisu}
    ,   REPLACE( #{base_date}, '-', '' ) 
    ,   ${method_file_id}
    ,   ${jisu_file_id}
    ,   #{req_content}
    ,   #{status}

    ,   #{user_id}
    ,   now() 
    ,   #{user_id}
    ,   now() 
)
