mapper.select:
SELECT BAS.F16013 /*단축코드*/ ,BAS.F16002 /*한글종목명*/ ,BAS.LARGE_TYPE /*지수대분류(FNGUIDE, KRX, KIS, KAP)*/ ,BAS.MIDDLE_TYPE /*지수중분류(FNGUIDE, WISEFN)*/ ,BAS.MARKET_ID /*시장 ID*/ ,HIS.F12506 /*입회일*/ ,HIS.F15009 /*시가*/ ,HIS.F15010 /*고가*/ ,HIS.F15011 /*저가*/ ,HIS.F15001 /*현재가*/ ,HIS.F15472 /*대비*/ ,HIS.F15006 /*등락구분(1:상한/2:상승/3:보합/4:하한/5:하락/6:기세상한/7:기세상승/8:기세하한/9:기세하락)*/ ,HIS.F15004 /*등락율*/ ,HIS.F15015 /*거래량*/ ,HIS.F15023 /*거래대금*/ FROM (SELECT * FROM kc_etp.td_index_basic WHERE F16013 = (SELECT MAX(F16013) FROM kc_etp.td_index_basic
WHERE large_type=#{large_type}
) ) BAS, kc_etp.td_index_hist HIS WHERE BAS.F16013 = HIS.F16013
AND BAS.LARGE_TYPE = #{large_type}
AND BAS.MARKET_ID = HIS.MARKET_ID ORDER BY HIS.F12506 DESC limit 0, 1

SELECT BAS.F16002 /*한글종목명*/ ,BAS.F16013 /*단축코드*/ ,REQ.INST_CD /*기관코드*/ ,REQ.INST_NAME /*기관명*/ ,date_format(REQ.REG_TIME, '%Y-%m-%d') REG_TIME /*등록일*/ ,REQ.REQ_FLAG /*승인여부*/ ,REQ.REG_ID /*등록자*/ ,'' AS F16012 /* 국제표준코드 */ , BAS.F16013 AS F16257 /* ETP기초지수코드 */ , LPAD( SUBSTR( BAS.MARKET_ID, 2 ), 3, '0' ) AS F34239 /* ETP기초지수MID */ FROM (SELECT A.INST_CD ,A.REG_TIME ,A.REQ_FLAG ,A.REG_ID ,A.JISU_ID ,B.INST_NAME FROM tm_jisu_share_req A, tm_user_domain B WHERE A.INST_CD = B.INST_CD) REQ LEFT OUTER JOIN td_index_basic BAS ON BAS.F16013 = REQ.JISU_ID WHERE BAS.F16013 = REQ.JISU_ID
AND BAS.large_type = #{large_type}
AND BAS.F16013 = #{jisu_cd}
AND (REQ.REQ_FLAG = '1' OR REQ.REQ_FLAG = '0')

SELECT BAS.F16002 /*한글종목명*/ ,BAS.F16013 /*단축코드*/ ,REQ.INST_CD /*기관코드*/ ,REQ.INST_NAME /*기관명*/ ,date_format(REQ.REG_TIME, '%Y-%m-%d') REG_TIME /*등록일*/ ,REQ.REQ_FLAG /*승인여부*/ ,REQ.REG_ID /*등록자*/ ,(SELECT COUNT(*) FROM tm_user_member WHERE INST_CD = REQ.INST_CD ) SUBCNT /*구독자수*/ FROM (SELECT A.INST_CD ,A.REG_TIME ,A.REQ_FLAG ,A.REG_ID ,A.JISU_ID ,B.INST_NAME FROM tm_jisu_share_req A, tm_user_domain B WHERE A.INST_CD = B.INST_CD) REQ LEFT OUTER JOIN td_index_basic BAS ON BAS.F16013 = REQ.JISU_ID WHERE BAS.F16013 = REQ.JISU_ID AND BAS.large_type = #{large_type}
AND BAS.F16013 = #{jisu_cd}
AND REQ.REQ_FLAG = '2'

mapper.update:
UPDATE tm_jisu_share_req 

       REQ_FLAG = #{FLAG}
       ,UPD_ID = #{UPD_ID}
       ,UPD_TIME = NOW() 

 WHERE JISU_ID = #{JISU_ID}
   AND INST_CD = #{INST_CD}

mapper.select:
SELECT A.JISU_CD /*지수코드*/ ,A.JISU_NM /*지수명*/ ,date_format(A.IP_DT, '%Y-%m-%d') IP_DT /*입회일자*/ ,if(A.ANNO_DATE is null, '연동', '발표') ANNO_YN /*발표여부*/ ,A.INDEX_CAL_METHOD /*지수산출방식*/ ,A.MARKET_ID /*시장ID*/ ,A.ETP_MARKET_ID /*ETP시장구분*/ ,A.INST_CNT /*기관수*/ ,A.LARGE_TYPE /*지수대분류(FNGUIDE, KRX, KIS, KAP)*/ ,A.MIDDLE_TYPE /*지수중분류(FNGUIDE, WISEFN)*/ ,A.F16013 /* 단축코드 */ ,A.F15001 /* 현재가 */ ,A.F15007 /* 기준가 */ ,A.F15472 /* 대비 */ ,A.F15004 /* 등락률 */ ,(SELECT GROUP_CONCAT(F16002) FROM td_etp_basic WHERE F34239 = A.ETP_MARKET_ID AND F16257 = A.JISU_CD) as ETP_NM /* ETP 명 */
,A.F16012 AS F16012 /* 국제표준코드 */ ,A.F16257 AS F16257 /* ETP기초지수코드 */ ,A.F34239 AS F34239 /* ETP기초지수MID */ FROM (SELECT BAS.F16013 JISU_CD ,BAS.F16002 JISU_NM ,BAS.F12506 IP_DT ,BAS.ANNO_DATE ,BAS.INDEX_CAL_METHOD ,BAS.MARKET_ID ,BAS.LARGE_TYPE ,BAS.MIDDLE_TYPE ,BAS.F16013 /* 단축코드 */ ,BAS.F15001 /* 현재가 */ ,BAS.F15007 /* 기준가 */ ,BAS.F15472 /* 대비 */ ,BAS.F15004 /* 등락률 */ ,CAST(substr(BAS.MARKET_ID, 2) AS SIGNED) ETP_MARKET_ID ,(SELECT COUNT(*) FROM kc_etp.tm_jisu_share_req WHERE JISU_ID = BAS.F16013 AND REQ_FLAG = '2') INST_CNT
,'' AS F16012 /* 국제표준코드 */ , BAS.F16013 AS F16257 /* ETP기초지수코드 */ , LPAD( SUBSTR( BAS.MARKET_ID, 2 ), 3, '0' ) AS F34239 /* ETP기초지수MID */ FROM td_index_basic BAS
WHERE BAS.large_type = #{large_type}
) A

SELECT A.F12506 /*입회일자*/ ,A.F15009 /*시가*/ ,A.F15010 /*고가*/ ,A.F15011 /*저가*/ ,A.F15001 /*현재가*/ ,A.F15472 /*대비*/ ,A.F15006 /*등락구분(1:상한/2:상승/3:보합/4:하한/5:하락/6:기세상한/7:기세상승/8:기세하한/9:기세하락)*/ ,A.F15004 /*등락율*/ ,A.F15015 /*거래량*/ ,A.F15023 /*거래대금*/ ,A.F16013 /*단축코드*/ ,A.F16002 /*한글종목명*/ ,A.LARGE_TYPE /*지수대분류(FNGUIDE, KRX, KIS, KAP)*/ ,A.MIDDLE_TYPE /*지수중분류(FNGUIDE, WISEFN)*/ ,A.MARKET_ID /*시장 ID*/ FROM kc_etp.td_index_basic A, (SELECT A.F16013 ,A.MARKET_ID FROM (SELECT basic.F16013 ,basic.MARKET_ID , (SELECT a.F15009 FROM td_index_hist a where a.F12506 = (SELECT MAX(F12506) FROM td_index_hist WHERE F12506 >= date_format(DATE_SUB(now(), INTERVAL 1 YEAR), '%Y%m%d')) AND a.F16013 = basic.F16013 AND a.MARKET_ID = basic.MARKET_ID ) maxData , (SELECT a.F15009 FROM td_index_hist a where a.F12506 = (SELECT MIN(F12506) FROM td_index_hist WHERE F12506 >= date_format(DATE_SUB(now(), INTERVAL 1 YEAR), '%Y%m%d')) AND a.F16013 = basic.F16013 AND a.MARKET_ID = basic.MARKET_ID ) minData FROM td_index_basic basic
WHERE basic.large_type=#{large_type}
) A GROUP BY F16013, MARKET_ID order by max(ifnull(A.maxData/A.minData, 0)) desc limit 0, 1 )B WHERE A.F16013 = B.F16013 AND A.MARKET_ID = B.MARKET_ID

        SELECT a.F12506 as trd_dd, a.F15009 as close_idx
          FROM td_index_hist a
         INNER JOIN td_index_basic b
           on  a.F16013 = b.F16013
		  and  a.market_id = b.market_id

          and  b.large_type = #{large_type}

         WHERE a.F12506 >= DATE_FORMAT(DATE_SUB(NOW(), INTERVAL 1 YEAR), '%Y%m%d')
           AND a.F16013 = #{JISU_ID}
           AND a.market_id = #{MARKET_ID}
