mapper.select:
SELECT BAS.F16002 ,BAS.F16013 ,REQ.INST_CD ,REQ.INST_NAME ,date_format(REQ.REG_TIME, '%Y-%m-%d') REG_TIME ,REQ.REQ_FLAG ,REQ.REG_ID
,'' AS F16012 /* 국제표준코드 */ , BAS.F16013 AS F16257 /* ETP기초지수코드 */ , LPAD( SUBSTR( BAS.MARKET_ID, 2 ), 3, '0' ) AS F34239 /* ETP기초지수MID */ FROM (SELECT A.INST_CD ,A.REG_TIME ,A.REQ_FLAG ,A.REG_ID ,A.JISU_ID ,B.INST_NAME FROM tm_jisu_share_req A, tm_user_domain B WHERE A.INST_CD = B.INST_CD) REQ LEFT OUTER JOIN td_index_basic BAS ON BAS.F16013 = REQ.JISU_ID WHERE BAS.F16013 = REQ.JISU_ID AND BAS.large_type = #{large_type}
AND BAS.F16013 = #{jisu_cd}
AND (REQ.REQ_FLAG = '1' OR REQ.REQ_FLAG = '0')

SELECT BAS.F16002 ,BAS.F16013 ,REQ.INST_CD ,REQ.INST_NAME ,date_format(REQ.REG_TIME, '%Y-%m-%d') REG_TIME ,REQ.REQ_FLAG ,REQ.REG_ID ,(SELECT COUNT(*) FROM tm_user_member WHERE INST_CD = REQ.INST_CD ) SUBCNT /*구독자수*/ FROM (SELECT A.INST_CD ,A.REG_TIME ,A.REQ_FLAG ,A.REG_ID ,A.JISU_ID ,B.INST_NAME FROM tm_jisu_share_req A, tm_user_domain B WHERE A.INST_CD = B.INST_CD) REQ LEFT OUTER JOIN td_index_basic BAS ON BAS.F16013 = REQ.JISU_ID WHERE BAS.F16013 = REQ.JISU_ID AND BAS.large_type = #{large_type}
AND BAS.F16013 = #{jisu_cd}
AND REQ.REQ_FLAG = '2'

mapper.update:
UPDATE tm_jisu_share_req 

       REQ_FLAG = #{FLAG}
       ,UPD_ID = #{UPD_ID}
       ,UPD_TIME = NOW() 

 WHERE JISU_ID = #{JISU_ID}
   AND INST_CD = #{INST_CD}

mapper.select:
SELECT A.JISU_CD ,A.JISU_NM ,date_format(A.IP_DT, '%Y-%m-%d') IP_DT ,if(A.ANNO_DATE is null, '미발표', '발표') ANNO_YN ,A.INDEX_CAL_METHOD ,A.MARKET_ID ,A.ETP_MARKET_ID ,A.INST_CNT ,A.LARGE_TYPE ,A.MIDDLE_TYPE ,(SELECT GROUP_CONCAT(F16002) FROM td_etp_basic WHERE F34239 = A.ETP_MARKET_ID AND F16257 = A.JISU_CD) as ETP_NM
,A.F16012 AS F16012 /* 국제표준코드 */ ,A.F16257 AS F16257 /* ETP기초지수코드 */ ,A.F34239 AS F34239 /* ETP기초지수MID */ FROM (SELECT BAS.F16013 JISU_CD ,BAS.F16002 JISU_NM ,BAS.F12506 IP_DT ,BAS.ANNO_DATE ,BAS.INDEX_CAL_METHOD ,BAS.MARKET_ID ,BAS.LARGE_TYPE ,BAS.MIDDLE_TYPE ,CAST(substr(BAS.MARKET_ID, 2) AS SIGNED) ETP_MARKET_ID ,(SELECT COUNT(*) FROM kc_etp.tm_jisu_share_req WHERE JISU_ID = BAS.F16013 AND REQ_FLAG = '2') INST_CNT
,'' AS F16012 /* 국제표준코드 */ , BAS.F16013 AS F16257 /* ETP기초지수코드 */ , LPAD( SUBSTR( BAS.MARKET_ID, 2 ), 3, '0' ) AS F34239 /* ETP기초지수MID */ FROM td_index_basic BAS
WHERE BAS.large_type = #{large_type}
) A
