mapper.select:
SELECT BAS.F16002 ,BAS.F16013 ,REQ.INST_CD ,REQ.INST_NAME ,date_format(REQ.REG_TIME, '%Y-%m-%d') REG_TIME ,REQ.REQ_FLAG ,REQ.REG_ID FROM (SELECT A.INST_CD ,A.REG_TIME ,A.REQ_FLAG ,A.REG_ID ,A.JISU_ID ,B.INST_NAME FROM tm_jisu_share_req A, tm_domain_mast B WHERE A.INST_CD = B.INST_CD) REQ LEFT OUTER JOIN td_index_basic BAS ON BAS.F16013 = REQ.JISU_ID WHERE BAS.F16013 = REQ.JISU_ID AND BAS.large_type = #{large_type}
AND BAS.F16013 = #{jisu_cd}
AND (REQ.REQ_FLAG = '1' OR REQ.REQ_FLAG = '0')

SELECT BAS.F16002 ,BAS.F16013 ,REQ.INST_CD ,REQ.INST_NAME ,date_format(REQ.REG_TIME, '%Y-%m-%d') REG_TIME ,REQ.REQ_FLAG ,REQ.REG_ID FROM (SELECT A.INST_CD ,A.REG_TIME ,A.REQ_FLAG ,A.REG_ID ,A.JISU_ID ,B.INST_NAME FROM tm_jisu_share_req A, tm_domain_mast B WHERE A.INST_CD = B.INST_CD) REQ LEFT OUTER JOIN td_index_basic BAS ON BAS.F16013 = REQ.JISU_ID WHERE BAS.F16013 = REQ.JISU_ID AND BAS.large_type = #{large_type}
AND BAS.F16013 = #{jisu_cd}
AND REQ.REQ_FLAG = '2'

mapper.update:
UPDATE tm_jisu_share_req 

       REQ_FLAG = #{FLAG}
       ,UPD_ID = #{UPD_ID}
       ,UPD_TIME = NOW() 

 WHERE JISU_ID = #{JISU_ID}
   AND INST_CD = #{INST_CD}
