mapper.select:
SELECT A.type_cd, B.type_name, A.inst_cd, C.inst_name, 
    A.email, A.emailchk, A.name, A.hp_no, A.tel_no, C.krx_cd, C.large_type
  FROM tm_user_member A, tm_user_type B, tm_user_domain C 
  WHERE A.email = #{email}
  AND A.password = #{password}
  AND A.type_cd = B.type_cd AND A.inst_cd = C.inst_cd

SELECT *
  FROM tm_user_member
  WHERE email = #{email}
  AND type_cd = #{type_cd}
  AND inst_cd = #{inst_cd}

mapper.update:
UPDATE tm_user_member SET
  password = #{password},
  name = #{name},
  hp_no = #{hp_no},
  tel_no = #{tel_no}
  WHERE email = #{email}
