mapper.select:
SELECT /* simulation.getSimulDailyInGrpCd 그룹코드에 속한 일자별 지수를 조회한다. */
tm_simul_result_daily.grp_cd AS grp_cd /* 그룹코드(상위코드) */ , tm_simul_result_daily.scen_cd AS scen_cd /* 시나리오 코드 */ , tm_simul_mast.scen_name AS scen_name /* 시나리오 명 */ , tm_simul_mast.bench_index_nm AS bench_index_nm /* 벤치마크 인덱스 명 */
, DATE_FORMAT( tm_simul_result_daily.F12506 , '%Y.%m.%d' ) AS fmt_F12506 /* 입회일자 */ , tm_simul_result_daily.F12506 AS F12506 /* 입회일자 */ , tm_simul_result_daily.INDEX_RATE AS INDEX_RATE /* 지수 */ , tm_simul_result_daily.RETURN_VAL AS RETURN_VAL /* 결과 */ , tm_simul_result_daily.BM_RATE AS BM_RATE /* 벤치마크 지수 */ , tm_simul_result_daily.BM_RETURN AS BM_RETURN /* 벤치마크 결과 */
FROM tm_simul_result_daily , ( SELECT tm_simul_mast.grp_cd AS grp_cd /* 그룹코드(상위코드) */ , tm_simul_mast.scen_cd AS scen_cd /* 시나리오 코드 */ , tm_simul_mast.scen_name AS scen_name /* 시나리오명 */ , (
SELECT tm_code_dtl.com_dtl_name FROM tm_code_dtl WHERE tm_code_dtl.com_mst_cd = 'COM008' AND tm_code_dtl.com_dtl_cd = tm_simul_mast.bench_mark_cd ) AS bench_index_nm /* 벤치마크 인덱스 명 */ FROM tm_simul_mast WHERE 1 = 1 AND tm_simul_mast.grp_cd = #{grp_cd} AND tm_simul_mast.scen_cd IN (
#{item.scen_cd}
)
) tm_simul_mast WHERE 1 = 1 AND tm_simul_result_daily.grp_cd = tm_simul_mast.grp_cd AND tm_simul_result_daily.scen_cd = tm_simul_mast.scen_cd
ORDER BY tm_simul_result_daily.scen_cd , tm_simul_result_daily.F12506
