mapper.select:

SELECT  tm_simul_portfolio.rebalance_date AS F12506
  FROM  tm_simul_portfolio2 AS tm_simul_portfolio
 WHERE  tm_simul_portfolio.grp_cd    =   #{grp_cd}
   AND  tm_simul_portfolio.scen_cd   =   #{scen_cd}
 GROUP BY tm_simul_portfolio.rebalance_date

SELECT /* simulationBacktest.getSimulBenchMark tm_simul_result_daily 테이블 기준 td_index_hist 테이블에서 bench_mark 와 일치하는 정보를 조회한다. */
td_kospi_hist.F12506 AS F12506 /* 입회일자 */ , IFNULL( td_index_hist.F15001 , 0 ) AS F15001 /* 현재가 */ , td_cd_hist.F15175 AS F15175 /* CD 수익률*/ , td_kospi_hist.F15001 AS KOSPI_F15001 /* 코스피 지수 */ FROM ( SELECT td_index_hist.F16013 AS F16013 /* 단축코드 */ , td_index_hist.F12506 AS F12506 /* 입회일자 */
, td_index_hist.F15001 AS F15001 /* 현재가 */ FROM td_index_hist JOIN td_index_basic ON ( td_index_hist.F16013 = td_index_basic.F16013 AND td_index_hist.market_id = td_index_basic.market_id ) WHERE td_index_basic.F16013 = '1' AND td_index_basic.large_type = 'KRX' AND td_index_basic.middle_type = 'KSP'
/* start_year 이전 첫 영업일 이후 부터 시작 */ AND td_index_hist.F12506 >= #{first_date}
ORDER BY F12506
) td_kospi_hist
LEFT JOIN td_cd_hist ON ( td_cd_hist.F12506 = td_kospi_hist.F12506 ) LEFT JOIN ( SELECT td_index_hist.F16013 AS F16013 /* 단축코드 */ , td_index_hist.F12506 AS F12506 /* 입회일자 */
, td_index_hist.F15001 AS F15001 /* 현재가 */ FROM td_index_hist JOIN td_index_basic ON ( td_index_hist.F16013 = td_index_basic.F16013 AND td_index_hist.market_id = td_index_basic.market_id ) WHERE td_index_basic.F16013 = #{bench_index_cd01} AND td_index_basic.large_type = #{bench_index_cd02} AND td_index_basic.middle_type = #{bench_index_cd03}
/* start_year 이전 첫 영업일 이후 부터 시작 */ AND td_index_hist.F12506 >= #{first_date}
ORDER BY F12506
) td_index_hist
ON ( td_index_hist.F12506 = td_kospi_hist.F12506 )
